import React, { Component } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity
} from "react-native";
import Header from "../components/header";

var d = new Date();

const getCurrentDate = () => {
  var date = new Date().getDate();
  var month = new Date().getMonth() + 1;
  var year = new Date().getFullYear();

  Alert.alert(date + "-" + month + "-" + year);
  // You can turn it in to your desired format
  return date + "-" + month + "-" + year; //format: dd-mm-yyyy;
};

export default class Home extends React.Component {
  render() {
    return (
      <View>
        <Header />
        <View style={styles.container}>
          <Text style={styles.Welcome}> Welcome Back</Text>
          <Text style={styles.Date}>Today is [Current Date]</Text>
          <Text style={styles.Current}>Current Monthly Budget: </Text>
          <Text style={styles.Budget}>$4,000</Text>

          <Text style={styles.Current}>Current Daily Budget: </Text>
          <Text style={styles.Spending}>$133</Text>

          <Text style={styles.Body}>
            <Text style={styles.red}>Uh oh</Text>
            ...It looks like you are currently [-$difference] according to your
            current budget. lets fix that (if statement whether or not you are
            above or below a budget)
          </Text>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    alignItems: "center"
    // justifyContent: "center"
  },
  Current: {
    marginTop: 40,
    fontSize: 30,
    alignItems: "center"
  },
  Welcome: {
    marginTop: 70,
    fontSize: 30,
    alignItems: "center"
  },
  Date: {
    marginTop: 10,
    fontSize: 20,
    alignItems: "center"
  },
  Budget: {
    marginTop: 10,
    fontSize: 50,
    color: "green",
    alignItems: "center"
  },
  Spending: {
    marginTop: 10,
    fontSize: 50,
    color: "red",
    alignItems: "center"
  },
  Body: {
    marginTop: 40,
    fontSize: 20,
    alignItems: "center",
    textAlign: "center"
  },
  red: {
    marginTop: 40,
    fontSize: 20,
    color: "red",
    alignItems: "center"
  }
});
