import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Button,
  Image,
  Alert
} from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createDrawerNavigator } from "@react-navigation/drawer";
import Icon from "react-native-vector-icons/Ionicons";
// import Login from "./screens/login";
import Home from "./bottomTab/home";
import Account from "./bottomTab/accountScreen";
import Budget from "./bottomTab/budget";
import BudgetHistory from "./bottomTab/budgetHistory";
import Manage from "./bottomTab/manage";
import Spending from "./bottomTab/spending";
import Forgot from "./screens/forgot";
import ExpenseItem from "./bottomTab/expenseItem";
import AddExpense from "./bottomTab/addExpense";
import ViewExpenses from "./bottomTab/viewExpenses";
import { ScrollView, FlatList } from "react-native-gesture-handler";
import viewExpenses from "./bottomTab/viewExpenses";
import {
  VictoryPie,
  VictoryBar,
  VictoryChart,
  VictoryTheme,
  VictoryLine
} from "victory-native";
import DropDownPicker from "react-native-dropdown-picker";

const Tab = createBottomTabNavigator();
const chart_wh = 250;
const series = [123, 321, 123, 789, 537];
const sliceColor = ["#F44336", "#2196F3", "#FFEB3B", "#4CAF50", "#FF9800"];
var total = 0;
var name;

class Sign extends React.Component {
  state = {
    email: "",
    phone: "",
    userName: "",
    password: "",
    confirmPassword: ""
  };
  render() {
    return (
      <View style={styles.SignUpcontainer}>
        <Text style={styles.logo}>Budget Manager</Text>
        <Text style={styles.logo2}>Pro</Text>
        <View style={styles.inputView}>
          <TextInput
            style={styles.inputText}
            placeholder="Email"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ email: text })}
          />
        </View>
        <View style={styles.inputView}>
          <TextInput
            style={styles.inputText}
            placeholder="Phone"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ phone: text })}
          />
        </View>
        <View style={styles.inputView}>
          <TextInput
            style={styles.inputText}
            placeholder="UserName"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ userName: text })}
          />
        </View>

        <View style={styles.inputView}>
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Password"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ password: text })}
          />
        </View>
        <View style={styles.inputView}>
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Confirm Password"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ confirmPassword: text })}
          />
        </View>

        <TouchableOpacity
          style={styles.loginBtn}
          onPress={async () => {
            if (
              this.state.password == this.state.confirmPassword &&
              this.state.password != ""
            ) {
              let response = await fetch(
                "https://budgetmanagerpro.herokuapp.com/api/users",
                {
                  method: "POST",
                  headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json"
                  },
                  body: JSON.stringify({
                    email: this.state.email,
                    phone: this.state.phone,
                    userName: this.state.userName,
                    password: this.state.password
                  })
                }
              );
              let user = await response.json();

              this.props.navigation.navigate("Login");
              Alert.alert("Yay!", "You've Signed Up!");
            } else {
              Alert.alert("Error", "Passwords do not match");
            }
            console.log(this.state.password);
            console.log(this.state.confirmPassword);
          }}
        >
          <Text style={styles.SignUp2}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

class Login extends React.Component {
  state = {
    email: "",
    password: ""
  };

  render() {
    return (
      <View style={styles.LoginContainer}>
        <Text style={styles.LoginLogo}>Budget Manager</Text>
        <Text style={styles.logo2}>Pro</Text>
        <View style={styles.inputView}>
          <TextInput
            style={styles.inputText}
            placeholder="Email"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ email: text })}
          />
        </View>
        <View style={styles.inputView}>
          <TextInput
            secureTextEntry
            style={styles.inputText}
            placeholder="Password"
            placeholderTextColor="#003f5c"
            onChangeText={text => this.setState({ password: text })}
          />
        </View>
        <TouchableOpacity
          onPress={() => this.props.navigation.navigate("ForgotPassword")}
        >
          <Text style={styles.forgot}>Forgot Password?</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.loginBtn}
          onPress={async () => {
            let response = await fetch(
              "https://budgetmanagerpro.herokuapp.com/auth/login",
              {
                method: "POST",
                headers: {
                  Accept: "application/json",
                  "Content-Type": "application/json"
                },
                body: JSON.stringify({
                  email: this.state.email,
                  password: this.state.password
                })
              }
            );
            let user = await response.json();
            console.log(user.sucess);

            if (user.success) {
              this.props.navigation.navigate("MyTabs", {
                paramKey: user
              });
            } else {
              Alert.alert("Error!", "Email or Password is Incorrect", [
                {
                  text: "Try again"
                }
              ]);
            }
          }}
        >
          <Text style={styles.loginText}>LOGIN</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={() => this.props.navigation.navigate("SignUp")}
        >
          <Text style={styles.SignUp}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    );
  }
}

function ProfileScreen({ navigation }) {
  return (
    <ScrollView>
      <View style={styles.header}></View>
      <Image style={styles.avatar} source={require("./images/Profile.png")} />
      <View style={styles.body}>
        <View style={styles.bodyContent}>
          <View style={styles.buttonContainer}>
            <Text>[Name]</Text>
          </View>
          <View style={styles.buttonContainer}>
            <Text>[Email]</Text>
          </View>
          <View style={styles.buttonContainer}>
            <Text>[Address]</Text>
          </View>
